mapper.select:
select * from(select rownum rn, b.*,e.empname,u.name,l.linename from BAS_SHUTTLEBUS b inner join SY_EMP e on b.operatorid=e.id inner join SY_UNITS u on b.operationunitid=u.id inner join LOG_Logistics l on b.lineid=l.id
and LICENSEPLATEINT like concat(concat('%',#{licenseplateint,jdbcType=VARCHAR}),'%')
and b.DRIVER like concat(concat('%',#{driver,jdbcType=VARCHAR}),'%')
and CARRIER like concat(concat('%',#{carrier,jdbcType=VARCHAR}),'%')
) where rn between #{page} and #{rows}
