mapper.select:
select * from ( select rownum rn,wr.*,un.name name,emp.empname empname from dis_workordersign wr inner join sy_units un on wr.inputid=un.id inner join sy_emp emp on wr.inputpersonid=emp.id
and wr.worksheetno like concat(concat('%',#{worksheetno}),'%')
and empname like concat(concat('%',#{couriername}),'%')
and name like concat(concat('%',#{recipient}),'%')
) where rn between #{page} and #{rows}

select * from ( select rownum rn,wr.*,un.name name,un2.name as uname,emp.empname empname from dis_workordersign wr inner join sy_units un on wr.inputid=un.id inner join sy_emp emp on wr.inputpersonid=emp.id inner join sy_units un2 on wr.signunit=un2.id
and wr.id like concat(concat('%',#{id}),'%')
and wr.worksheetno like concat(concat('%',#{worksheetno}),'%')
) where rn between #{page} and #{rows}
